INSERT INTO `page_context` VALUES ('709', '22', '数据科学与大数据技术专业介绍（2018年） ', 'c2', '<div class=\"content\">\r\n            <!--您现在的位置开始-->\r\n            \r\n            <!--您现在的位置结束-->\r\n            <!--站内广告开始-->\r\n            <div class=\"area_ad mtop10\">\r\n                \r\n            </div>\r\n            <!--站内广告结束-->\r\n            <!--网站正文开始-->\r\n            <div class=\"article mtop10\">\r\n                <div class=\"article_line\">\r\n                </div>\r\n                <div class=\"article_content\">\r\n                    <h1>\r\n                        <span><font style=\"font-style: normal; font-weight: normal;\">数据科学与大数据技术专业介绍（2018年）</font></span>\r\n                    </h1>\r\n               \r\n                    <!--网站内容开始-->\r\n                    <div class=\"article_content_list\" id=\"fontzoom\">\r\n                    <div id=\"articleContnet\">\r\n                    \r\n      \r\n     <p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0pt; line-height: 150%; text-indent: 24pt; mso-char-indent-count: 2.0;\"><span style=\"line-height: 150%; font-family: 仿宋_gb2312; font-size: 12pt; mso-hansi-font-family: 仿宋_gb2312;\"><font color=\"#000000\">数据科学与大数据技术专业被称为“<span lang=\"EN-US\">21</span>世纪最热门的专业”。当前信息技术正在向数据技术转变，国家“十三五”规划中也将发展大数据产业作为国家发展战略，大数据人才的缺口日益增大。<span lang=\"EN-US\"><o:p></o:p></span></font></span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0pt; line-height: 150%; text-indent: 24pt; mso-char-indent-count: 2.0;\"><span style=\"line-height: 150%; font-family: 仿宋_gb2312; font-size: 12pt; mso-hansi-font-family: 仿宋_gb2312;\"><font color=\"#000000\">数据科学与大数据技术专业主要学习软件工程和计算机科学的基础理论、大数据采集、处理、分析、展现以及应用的基本理论、基本知识和基本技能，还要学习背景方向专业知识，掌握系统科学、软件工程和大数据处理技术，具备实践能力和创新意识。获得大数据采集、处理、分析与展现的基本能力<span lang=\"EN-US\">,</span>并能在人工智能、物联网和图像处理等领域从事相关的大数据产品的研发工作。<span lang=\"EN-US\"><o:p></o:p></span></font></span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0pt; line-height: 150%; text-indent: 24pt; mso-char-indent-count: 2.0;\"><span style=\"line-height: 150%; font-family: 仿宋_gb2312; font-size: 12pt; mso-hansi-font-family: 仿宋_gb2312;\"><font color=\"#000000\">毕业生能在科研院所、企事业单位和行政管理部门等从事大数据采集、处理、分析、展现以及应用，也可在高校或科研机构从事科研和教学工作。<span lang=\"EN-US\"><o:p></o:p></span></font></span></p>\r\n    \r\n   \r\n                    </div>\r\n                        <br>\r\n                        \r\n                        <div class=\"page_css\">\r\n                            <span class=\"pagecss\" id=\"pe100_page_contentpage\"></span></div>\r\n                        <div class=\"clear\">\r\n                        </div>\r\n<!--                        <div class=\"article_mood\">\r\n                            <div id=\"moodVote\">\r\n  <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n    <tbody>\r\n      <tr>\r\n        <td align=\"center\" valign=\"bottom\"><img src=\"/Images/MoodIcon/motion_01.gif\"></td>\r\n        <td align=\"center\" valign=\"bottom\"><img src=\"/Images/MoodIcon/motion_02.gif\"></td>\r\n        <td align=\"center\" valign=\"bottom\"><img src=\"/Images/MoodIcon/motion_03.gif\"></td>\r\n        <td align=\"center\" valign=\"bottom\"><img src=\"/Images/MoodIcon/motion_04.gif\"></td>\r\n        <td align=\"center\" valign=\"bottom\"><img src=\"/Images/MoodIcon/motion_05.gif\"></td>\r\n        <td align=\"center\" valign=\"bottom\"><img src=\"/Images/MoodIcon/motion_06.gif\"></td>\r\n        <td align=\"center\" valign=\"bottom\"><img src=\"/Images/MoodIcon/motion_07.gif\"></td>\r\n        <td align=\"center\" valign=\"bottom\"><img src=\"/Images/MoodIcon/motion_08.gif\"></td>\r\n      </tr>\r\n      <tr>\r\n        <td align=\"center\" valign=\"bottom\">感动</td>\r\n        <td align=\"center\" valign=\"bottom\">同情</td>\r\n        <td align=\"center\" valign=\"bottom\">无聊</td>\r\n        <td align=\"center\" valign=\"bottom\">愤怒</td>\r\n        <td align=\"center\" valign=\"bottom\">搞笑</td>\r\n        <td align=\"center\" valign=\"bottom\">难过</