INSERT INTO `page_context` VALUES ('1921', '19', '南昌航空大学软件学院工会简介', '无', '<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-size: 12pt;\"></span><span style=\"font-size: 12pt;\">南昌航空大学软件学院工会委员会是学校和学院党委的共同领导下的教职工的群众性组织，在学校党政的大力支持及上级工会的悉心指导下，依据《中华人民共和国工会法》、《中国工会章程》等法律法规，全面履行“维护、建设、参与、教育”四项职能，广泛发动并积极依靠广大工会会员创造性地开展工作。曾荣获全国模范职工之家、全国教科文卫体系统先进工会组织、全国厂务公开民主管理先进单位等荣誉称号。</span></p><p><span style=\"line-height: 24px; font-size: 12pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;坚持围绕学校工作大局，夯实“软件硬件”基础，打造好教代会提案、校（院）务公开、法律援助等品牌栏目，加快推进“学习型、服务型、创新型、开放型”的工会组织建设，积极实施自身建设强化工程、民主管理推进工程、师德师风建设工程、青年教师培养工程、身心健康培育工程、民生改善保障工程，全面维护教职工合法权益，不断推动工会工作科学发展、创新发展、和谐发展，为推进和谐校园建设、实现学校新跨越而建功立业。</span></p><p><br/></p>', '工会委员会', '2019-01-03 00:00:00', '654', '1');
