DROP TRIGGER IF EXISTS `tri_insert`;
DELIMITER ;;
CREATE TRIGGER `tri_insert` AFTER INSERT ON `achievement_info` FOR EACH ROW begin
	declare a float;
    declare b varchar(10);
    set a = (select credit from course_info where courseno=new.courseno);
    set b = (select stuid from hangingsection where stuid=new.stuno);
	if (new.totalresults) < '60' then
		  if b is null then
          else
			update hangingsection set credit=credit+a where stuid=b;
	    end if;
     end if;
end
;;
DELIMITER ;
